import com.sun.javadoc.Doc;

public class DocProcessor {

    /**
     * Processes the documentation comment of a Java class or interface.
     *
     * @param doc the Doc object representing the class or interface
     * @return a string containing the first sentence of the documentation comment
     */
    public String processDoc(Doc doc) {
        if (doc == null || doc.commentText().isEmpty()) {
            return "No documentation available.";
        }
        String comment = doc.commentText();
        int endOfFirstSentence = comment.indexOf('.');
        if (endOfFirstSentence != -1) {
            return comment.substring(0, endOfFirstSentence + 1);
        } else {
            return comment;
        }
    }
}

