        PackageDoc[] packages = root.specifiedPackages();
        for (PackageDoc packageDoc : packages) {
            System.out.println("Package: " + packageDoc.name());
            System.out.println("Annotations: " + packageDoc.annotations().length);
            System.out.println("Classes: " + packageDoc.allClasses().length);
        }
        return true;
    }
}

