import com.sun.jarsigner.ContentSignerParameters;

public class ContentSignerExample {

    public String getSignatureAlgorithm(ContentSignerParameters parameters) {
        // Assuming the ContentSignerParameters object has a method to retrieve signature algorithm
        return parameters.getSignatureAlgorithm();
    }

    public byte[] getSignature(ContentSignerParameters parameters) {
        // Assuming the ContentSignerParameters object has a method to retrieve the signature
        return parameters.getSignature();
    }

    public void printSignerDetails(ContentSignerParameters parameters) {
        // Print details using the ContentSignerParameters methods
        System.out.println("Signature Algorithm: " + getSignatureAlgorithm(parameters));
        System.out.println("Signature: " + new String(getSignature(parameters)));
    }
}
