import com.sun.javadoc.RootDoc;

public class OldJavadocUtil {

    public static boolean start(RootDoc root) {
        System.out.println("Processing documentation...");
        // Iterate over all the classes and print their names
        for (var clazz : root.classes()) {
            System.out.println("Class: " + clazz.qualifiedName());
            // Iterate over all methods in the class
            for (var method : clazz.methods()) {
                System.out.println("  Method: " + method.name());
            }
        }
        return true;
    }
}

import com.sun.javadoc.ClassDoc;
import com.sun.javadoc.MethodDoc;
import com.sun.javadoc.RootDoc;
import com.sun.javadoc.Tag;

public class DocAnalyzer {

    public static boolean start(RootDoc root) {
        ClassDoc[] classes = root.classes();
        for (ClassDoc classDoc : classes) {
            System.out.println("Class: " + classDoc.name());
            MethodDoc[] methods = classDoc.methods();
            for (MethodDoc method : methods) {
                System.out.println("  Method: " + method.name());
                Tag[] tags = method.tags();
                for (Tag tag : tags) {
                    System.out.println("    Tag: " + tag.name() + " - " + tag.text());
                }
            }
        }
        return true;
    }
}

import com.sun.javadoc.RootDoc;

public class DocProcessor {

    public static boolean processRootDoc(RootDoc root) {
        if (root == null) {
            return false;
        }
        // Process each class documentation
        for (com.sun.javadoc.ClassDoc classDoc : root.classes()) {
            System.out.println("Class: " + classDoc.qualifiedName());
            // Process methods of the class
            for (com.sun.javadoc.MethodDoc methodDoc : classDoc.methods()) {
                System.out.println("Method: " + methodDoc.name());
            }
        }
        return true;
    }

    public static void main(String[] args) {
        // This main method would typically be run by a Javadoc tool and not directly.
    }

}

