import com.sun.javadoc.ThrowsTag;
import com.sun.javadoc.ClassDoc;
import com.sun.javadoc.RootDoc;

public class ThrowsTagExample {

    /**
     * This method retrieves all the exception information from the given
     * class documentation using the ThrowsTag.
     *
     * @param root The RootDoc object that contains the documentation of the classes.
     * @param className The name of the class to retrieve exception information from.
     */
    public void printExceptionInfo(RootDoc root, String className) {
        ClassDoc[] classes = root.classes();
        for (ClassDoc classDoc : classes) {
            if (classDoc.name().equals(className)) {
                ThrowsTag[] throwsTags = classDoc.throwsTags();
                for (ThrowsTag throwsTag : throwsTags) {
