import com.sun.javadoc.Type;
import com.sun.javadoc.WildcardType;

public class WildcardTypeExample {

    public String describeWildcardType(WildcardType wildcardType) {
        StringBuilder description = new StringBuilder("WildcardType: ");

        Type[] extendsBounds = wildcardType.extendsBounds();
        if (extendsBounds.length > 0) {
            description.append(" extends ");
            for (Type type : extendsBounds) {
                description.append(type.qualifiedTypeName()).append(" ");
            }
        }

        Type[] superBounds = wildcardType.superBounds();
        if (superBounds.length > 0) {
            description.append(" super ");
            for (Type type : superBounds) {
                description.append(type.qualifiedTypeName()).append(" ");
            }
        }

        return description.toString().trim();
    }
}

