import java.security.acl.Acl;
import java.security.acl.AclEntry;
import java.security.acl.Permission;
import java.security.acl.NotOwnerException;
import java.security.Principal;
import java.util.Enumeration;
import java.util.Vector;

public class AccessControlList implements Acl {

    private Vector<AclEntry> aclEntries;
    private String name;
    private Principal owner;

    public AccessControlList(String name, Principal owner) {
        this.aclEntries = new Vector<>();
        this.name = name;
        this.owner = owner;
    }

    @Override
    public void setName(Principal caller, String name) throws NotOwnerException {
        if (!caller.equals(owner)) {
            throw new NotOwnerException();
        }
        this.name = name;
    }

    @Override
    public String getName() {
        return this.name;
    }

    @Override
    public boolean addEntry(Principal caller, AclEntry entry) throws NotOwnerException {
        if (!caller.equals(owner)) {
            throw new NotOwnerException();
        }
        if (!aclEntries.contains(entry)) {
            aclEntries.add(entry);
            return true;
        }
        return false;
    }

    @Override
    public boolean removeEntry(Principal caller, AclEntry entry) throws NotOwnerException {
        if (!caller.equals(owner)) {
            throw new NotOwnerException();
        }
        return aclEntries.remove(entry);
    }

    @Override
    public Enumeration<Permission> getPermissions(Principal user) {
        Vector<Permission> permissions = new Vector<>();
        for (AclEntry entry : aclEntries) {
            if (entry.getPrincipal().equals(user)) {
                Enumeration<Permission> entryPermissions = entry.permissions();
                while (entryPermissions.hasMoreElements()) {
                    permissions.add(entryPermissions.nextElement());
                }
            }
        }
        return permissions.elements();
    }

    @Override
    public Enumeration<AclEntry> entries() {
        return aclEntries.elements();
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();
        sb.append("Acl Name: ").append(name).append("\n");
        for (AclEntry entry : aclEntries) {
            sb.append(entry.toString()).append("\n");
        }
        return sb.toString();
    }
}

