import java.util.jar.JarOutputStream;
import java.util.jar.JarEntry;
import java.util.jar.Pack200;
import java.util.jar.Pack200.Packer;
import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.HashMap;
import java.util.Map;

public class JarCompressor {

    public static void compressJar(File inputJar, File outputJar) throws IOException {
        try (JarOutputStream jos = new JarOutputStream(new FileOutputStream(outputJar))) {
            Packer packer = Pack200.newPacker();
            Map<String, String> props = new HashMap<>();
            // Set the desired properties for the packer
            props.put(Packer.EFFORT, "9"); // Maximum effort
            props.put(Packer.DEFLATE_HINT, Packer.TRUE);
            props.put(Packer.KEEP_FILE_ORDER, Packer.TRUE);
            packer.properties().putAll(props);
            packer.pack(inputJar, jos);
        }
    }

    public static void main(String[] args) {
        try {
            File inputJar = new File("input.jar");
            File outputJar = new File("output.pack");
            compressJar(inputJar, outputJar);
            System.out.println("Jar compression completed.");
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

