import jdk.nashorn.api.scripting.ClassFilter;
import javax.script.ScriptEngine;
import javax.script.ScriptEngineManager;
import javax.script.ScriptException;

public class NashornClassFilterExample {

    public static void main(String[] args) {
        ScriptEngineManager manager = new ScriptEngineManager();
        ScriptEngine engine = manager.getEngineByName("nashorn");

        // Creating a custom ClassFilter
        ClassFilter myClassFilter = new ClassFilter() {
            @Override
            public boolean exposeToScripts(String className) {
                // Only allow classes in "java.util" package
                return className.startsWith("java.util");
            }
        };

        // Attaching the ClassFilter to the engine
        engine.getContext().setAttribute("ClassFilter", myClassFilter, ScriptEngine.ENGINE_SCOPE);

        try {
            // This script should work, as java.util packages are allowed
            engine.eval("var list = new java.util.ArrayList(); list.add('Hello Nashorn');");

            // This script should fail, since java.lang packages are not allowed
            engine.eval("var system = java.lang.System;");

        } catch (ScriptException e) {
            e.printStackTrace();
        }
    }
}

