import jdk.nashorn.api.tree.CompilationUnitTree;
import jdk.nashorn.api.tree.ForLoopTree;
import jdk.nashorn.api.tree.Tree;
import jdk.nashorn.api.tree.TreeVisitor;
import jdk.nashorn.api.tree.Tree.Kind;
import jdk.nashorn.api.scripting.NashornScriptEngineFactory;
import javax.script.ScriptEngine;
import javax.script.ScriptEngineManager;
import java.util.List;

public class ForLoopAnalyzer {

    public static void main(String[] args) {
        String script = "for (var i = 0; i < 10; i++) { print(i); }";
        analyzeForLoops(script);
    }

    public static void analyzeForLoops(String script) {
        ScriptEngineManager manager = new ScriptEngineManager();
        ScriptEngine engine = new NashornScriptEngineFactory().getScriptEngine();

        try {
            CompilationUnitTree tree = (CompilationUnitTree) engine.eval(script);
            for (Tree node : tree.getSourceElements()) {
                if (node.getKind() == Kind.FOR_LOOP) {
                    ForLoopTree forLoop = (ForLoopTree) node;
                    System.out.println("For Loop Found: ");
                    System.out.println("Initializer: " + forLoop.getInitializer());
                    System.out.println("Condition: " + forLoop.getCondition());
                    System.out.println("Update: " + forLoop.getUpdate());
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

