import jdk.nashorn.api.tree.LineMap;

public class OldLineMapExample {

    private long[] lineStartOffsets;

    public OldLineMapExample(String code) {
        this.lineStartOffsets = computeLineStartOffsets(code);
    }

    private long[] computeLineStartOffsets(String code) {
        java.util.List<Long> lineOffsets = new java.util.ArrayList<>();
        lineOffsets.add(0L);  // first line starts at 0
        for (int i = 0; i < code.length(); i++) {
            if (code.charAt(i) == '\n') {
                lineOffsets.add((long) i + 1);
            }
        }
        long[] result = new long[lineOffsets.size()];
        for (int i = 0; i < lineOffsets.size(); i++) {
            result[i] = lineOffsets.get(i);
        }
        return result;
    }

    public int getLineNumber(long position) {
        for (int i = 0; i < lineStartOffsets.length; i++) {
            if (position < lineStartOffsets[i]) {
                return i;
            }
        }
        return lineStartOffsets.length;
    }

    public int getColumnNumber(long position) {
        int lineNumber = getLineNumber(position);
        if (lineNumber > 0) {
            return (int) (position - lineStartOffsets[lineNumber - 1]) + 1;
        }
        return (int) position + 1;
    }

    public static void main(String[] args) {
        String code = "int x = 10;\nSystem.out.println(x);\n";
        OldLineMapExample lineMap = new OldLineMapExample(code);
        long position = 15;  // example position
        System.out.println("Line Number: " + lineMap.getLineNumber(position));
        System.out.println("Column Number: " + lineMap.getColumnNumber(position));
    }
}

