import jdk.nashorn.api.tree.BinaryTree;
import jdk.nashorn.api.tree.LiteralTree;

public class TreeEvaluator implements TreeVisitor<Integer, Void> {
    @Override
    public Integer visitBinary(BinaryTree node, Void unused) {
        int leftValue = node.getLeftOperand().accept(this, null);
        int rightValue = node.getRightOperand().accept(this, null);
        switch (node.getKind()) {
            case PLUS:
                return leftValue + rightValue;
            case MINUS:
                return leftValue - rightValue;
            case MULTIPLY:
                return leftValue * rightValue;
            case DIVIDE:
                return leftValue / rightValue;
            default:
                throw new UnsupportedOperationException("Operation not supported");
        }
    }

    @Override
    public Integer visitLiteral(LiteralTree node, Void unused) {
        return (Integer) node.getValue();
    }

    @Override
    public Integer visitTree(Tree node, Void unused) {
        throw new UnsupportedOperationException("Tree type not supported");
    }

    // Other visit methods can be implemented as needed...

