import org.xml.sax.Parser;
import org.xml.sax.InputSource;
import org.xml.sax.SAXException;
import org.xml.sax.helpers.DefaultHandler;
import java.io.IOException;
import java.io.StringReader;

public class OldXMLParser extends DefaultHandler {
    private Parser parser;

    public OldXMLParser(Parser parser) {
        this.parser = parser;
        parser.setDocumentHandler(this);
    }

    public void parse(String xmlData) throws SAXException, IOException {
        InputSource inputSource = new InputSource(new StringReader(xmlData));
        parser.parse(inputSource);
    }

    @Override
    public void startDocument() throws SAXException {
        System.out.println("Start of XML Document");
    }

    @Override
    public void endDocument() throws SAXException {
        System.out.println("End of XML Document");
    }

    @Override
    public void startElement(String name, org.xml.sax.AttributeList atts) throws SAXException {
        System.out.println("Start Element: " + name);
    }

    @Override
    public void endElement(String name) throws SAXException {
        System.out.println("End Element: " + name);
    }

    public static void main(String[] args) {
        try {
            Parser parser = new org.apache.xerces.parsers.SAXParser(); // Assuming the old SAXParser implementation
            OldXMLParser oldXMLParser = new OldXMLParser(parser);
            String xmlData = "<note><to>Tove</to><from>Jani</from><heading>Reminder</heading><body>Don't forget me this weekend!</body></note>";
            oldXMLParser.parse(xmlData);
        } catch (SAXException | IOException e) {
            e.printStackTrace();
        }
    }
}
